	};

	const onFinish = (values) => {
		console.log("Success:", values);
		console.log(values.username);
		console.log(values.description);
		setDataUser(values.username);
		setUserDescription(values.email);
		console.log(userDescription);

		let jwt = Cookies.get("token");
		const data = {
			text: values.text,
			id: jwt_decode(jwt).id,
		};

		console.log(jwt);

		fetch("https://api-minireseausocial.mathis-dyk.fr/posts", {
			method: "PUT",
			headers: {
				"Content-Type": "application/json",
				Authorization: `Bearer ${jwt}`,
			},
			body: JSON.stringify(data),
		})
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				// console.log(post.email);
				// console.log(post.username);
				// console.log("MAil" + post.email);
				// setDataUser(post.username);
				// setDataMail(post.email);
			})
			.then((posts) => {
				console.log(posts);
			});
	};

	return (
		<div>
			<h1>PostForm</h1>
		</div>
	);
};
export default PostFrom;
