import React, { useState, useEffect } from "react";

const Home = () => {
	const [dataPost, setDataPost] = useState("Empty");

	useEffect(() => {
		fetch("https://api-minireseausocial.mathis-dyk.fr/posts")
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				console.log("hello");
				setDataPost(post.text);
			});
	}, []);

	return (
		<div>
			<h1>Welcome on My Social Network</h1>
			<p>
				This website is a training to Redux and React. We use auth and routing to
				create a small social media website.
			</p>
			<h3>List of posts:</h3>
			{dataPost.map((el, key) => (
				<div key={key}>
					<h5>{post.text}</h5>
				</div>
			))}
		</div>
	);
};
export default Home;
