import { SET_POSTS, API_START, API_END, FETCH_POSTS } from "./types";

const postReducer = (state = {}, action) => {
	switch (action.type) {
		case SET_POSTS:
			return { data: action.payload };
		case API_START:
			if (action.payload === FETCH_POSTS) {
				return {
					...state,
					isFetching: true, // Shows loading state while fetching data from backend.
				};
			}
			break;
		case API_END:
			if (action.payload === FETCH_POSTS) {
				return {
					...state,
					isFetching: false,
				};
			}
			break;
		default:
			return state;
	}
};

export default postReducer;
