import { LOG_IN } from "./logType";

const initialState = {
	authenticated: false,
};

const userReducer = (state = initialState, action) => {
	switch (action.type) {
		case LOG_IN:
			return {
				...state,
				authenticated: true,
			};
		default:
			return state;
	}
};

export default userReducer;
