import React, { useState, useEffect } from "react";
import Cookies from "js-cookie";
import PostForm from "../../components/PostForm/PostForm.jsx";

const Home = () => {
	console.log("Check cookies on home:" + Cookies.get("token"));
	console.log("Check cookies still home:" + Cookies.get("token"));
	const [dataPost, setDataPost] = useState([]);

	useEffect(() => {
		fetch("https://api-minireseausocial.mathis-dyk.fr/posts")
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				console.log("hello");
				setDataPost(post);
			});
	}, []);

	return (
		<div>
			<h1>Welcome on My Social Network</h1>
			<p>
				This website is a training to Redux and React. We use auth and routing to
				create a small social media website.
			</p>
			<PostForm />
			<h3>Latest posts:</h3>
			{dataPost.map((post, key) => (
				<div key={key}>
					<h5>{post.text}</h5>
				</div>
			))}
		</div>
	);
};
export default Home;
