const Book = () => {
	let { bookSlug } = useParams();
	console.log("Print books" + books);
	const [currentBook, setCurrentBook] = useState(null);

	useEffect((bookSlug) => {
		setCurrentBook(books.find((book) => book === bookSlug));
		console.log(bookSlug);
	}, []);
	return <p>{currentBook}</p>;
};
export default Book;
