//import React from "react";
import Cookies from "js-cookie";
import React, { useState, useEffect } from "react";
import { Form, Input, Button } from "antd";

const layout = {
	labelCol: { span: 4 },
	wrapperCol: { span: 8 },
};
const tailLayout = {
	wrapperCol: { offset: 4, span: 8 },
};

const Profile = () => {
	const onFinish = (values) => {
		console.log("Success:", values);
		console.log(values.username);
		console.log(values.description);

		const data = {
			username: values.username,
			email: values.email,
			password: values.password,
		};

		console.log("test:" + data);
		fetch("https://api-minireseausocial.mathis-dyk.fr/auth/local/register", {
			method: "post",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify(data),
		})
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				console.log(post.jwt);
				console.log(post.user);
				Cookies.set("token", post.jwt);
				console.log("Cookies get:" + Cookies.get("token"));
			})
			.then((posts) => {
				console.log(posts);
			});

		console.log("test:" + data);
	};

	const onFinishFailed = (errorInfo) => {
		console.log("Failed:", errorInfo);
	};

	const [dataUser, setDataUser] = useState("");
	const [dataMail, setDataMail] = useState("");

	useEffect(() => {
		let jwt = Cookies.get("token");

		const data = {
			username: "Laliluuuu",
			email: "lalilu.xx.fr",
			password: "1234567",
		};

		console.log("test:" + data);
		fetch("https://api-minireseausocial.mathis-dyk.fr/users/me", {
			method: "get",
			headers: {
				Authorization: `Bearer ${jwt}`,
				"Content-Type": "application/json",
			},
			// body: JSON.stringify(data),
		})
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				console.log(post.username);
				console.log(post.email);
				setDataUser(post.username);
				setDataMail(post.email);
				// console.log(post.user);
				// Cookies.set("token", post.jwt);
				console.log("Cookies get:" + Cookies.get("token"));
			})
			.then((posts) => {
				console.log(posts);
			});
	}, []);

	const editProfile = () => {
		console.log("lets edit the profile!!");
	};

	return (
		<div>
			<h1>My Profile</h1>
			<p>Username: {dataUser}</p>
			<p>Mail address: {dataMail}</p>
			<Form
				{...layout}
				name="basic"
				initialValues={{ remember: true }}
				onFinish={onFinish}
				onFinishFailed={onFinishFailed}
			>
				<Form.Item
					label="Username"
					name="username"
					rules={[{ required: true, message: "Please input your username!" }]}
				>
					<Input />
				</Form.Item>

				<Form.Item
					label="Email"
					name="email"
					rules={[{ required: true, message: "Please input your email!" }]}
				>
					<Input.Password />
				</Form.Item>

				<Form.Item
					{...tailLayout}
					name="remember"
					valuePropName="checked"
				></Form.Item>

				<Form.Item {...tailLayout}>
					<Button type="primary" htmlType="submit">
						Edit profile data
					</Button>
				</Form.Item>
			</Form>
		</div>
	);
};
export default Profile;
