import Cookies from "js-cookie";
import React, { useState, useEffect } from "react";
import jwt_decode from "jwt-decode";

const MyPosts = () => {
	//getMyPosts
	useEffect(() => {
		let jwt = Cookies.get("token");
		const myUsername = jwt_decode(jwt).username;
		const url = "https://api-minireseausocial.mathis-dyk.fr/user" + myUsername;

		fetch(url, {
			method: "get",
			headers: {
				Authorization: `Bearer ${jwt}`,
				"Content-Type": "application/json",
			},
		})
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				// console.log(post.username);
				// console.log(post.user);
				// console.log(post.email);
				// setDataUser(post.username);
				// setDataMail(post.email);
				// setDataDescription(post.description);
				console.log("Cookies get:" + Cookies.get("token"));
			})
			.then((posts) => {
				console.log(posts);
			});
	}, []);

	return (
		<div>
			<h1>My tweets:</h1>
		</div>
	);
};

export default MyPosts;
