//import React from "react";
import Cookies from "js-cookie";
import React, { useState, useEffect } from "react";

const Profile = () => {
	const [dataUser, setDataUser] = useState("");
	const [dataMail, setDataMail] = useState("");

	const onFinish = (values, authenticated) => {
		let jwt = Cookies.get("token");
		console.log("Success:", values);
		console.log(values.username);
		console.log(values.password);

		const data = {
			username: "Laliluuuu",
			email: "lalilu.xx.fr",
			password: "1234567",
		};

		console.log("test:" + data);
		fetch("https://api-minireseausocial.mathis-dyk.fr/users/me", {
			method: "get",
			headers: {
				Authorization: `Bearer ${jwt}`,
				"Content-Type": "application/json",
			},
			// body: JSON.stringify(data),
		})
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				console.log(post.username);
				console.log(post.email);
				setDataUser(post.username);
				setDataMail(post.email);
				// console.log(post.user);
				// Cookies.set("token", post.jwt);
				console.log("Cookies get:" + Cookies.get("token"));
				console.log("testss" + authenticated);
			})
			.then((posts) => {
				console.log(posts);
			});

		// console.log("test:" + data);
	};

	return (
		<div>
			<h1>My Profile</h1>
			<p>{dataUser}</p>
			<p>{dataMail}</p>
			<button onClick={onFinish}>Test fetch</button>
		</div>
	);
};

export default Profile;
