import React from "react";
import jwt_decode from "jwt-decode";
import { Input } from "antd";
import { Button } from "antd";

const PostFrom = () => {
	const sendTweet = () => {
		let tweet = document.getElementById("tweet").value;
		console.log(tweet);

		let jwt = Cookies.get("token");
		const data = {
			text: tweet,
			id: jwt_decode(jwt).id,
		};

		fetch("https://api-minireseausocial.mathis-dyk.fr/posts", {
			method: "PUT",
			headers: {
				"Content-Type": "application/json",
				Authorization: `Bearer ${jwt}`,
			},
			body: JSON.stringify(data),
		}).then((res) => res.json());
		// .then((post) => {
		// 	console.log(post);
		// console.log(post.email);
		// console.log(post.username);
		// console.log("MAil" + post.email);
		// setDataUser(post.username);
		// setDataMail(post.email);
		// 		})
		// 		.then((posts) => {
		// 			console.log(posts);
		// 		});
	};

	return (
		<div>
			<h3 style={{ marginLeft: "50px" }}>Let's tweet</h3>
			<Input
				id="tweet"
				type="text"
				style={{ width: "500px", marginLeft: "50px" }}
				placeholder="What's on your mind?"
			/>
			<Button onClick={sendTweet} type="primary">
				Send tweet
			</Button>
		</div>
	);
};
export default PostFrom;
