import React from "react";
import { Link } from "react-router-dom";
import { Form, Input, Button, Checkbox } from "antd";
import Cookies from "js-cookie";
import { Redirect } from "react-router-dom";
//import React, { useState, useEffect } from "react";

const layout = {
	labelCol: { span: 4 },
	wrapperCol: { span: 8 },
};
const tailLayout = {
	wrapperCol: { offset: 4, span: 8 },
};

const Home = (test) => {
	console.log("redirect");
	if ((test = true)) return <Redirect to="/login" />;
};

const Register = () => {
	const onFinish = (values) => {
		console.log("Success:", values);
		console.log(values.username);
		console.log(values.password);

		const data = {
			username: values.username,
			email: values.email,
			password: values.password,
		};

		console.log("test:" + data);
		fetch("https://api-minireseausocial.mathis-dyk.fr/auth/local/register", {
			method: "post",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify(data),
		})
			.then((res) => res.json())
			.then((post) => {
				console.log(post);
				console.log(post.jwt);
				Cookies.set("token", post.jwt);
				console.log("Cookies get:" + Cookies.get("token"));
			});

		console.log("test:" + data);
		return <Redirect to="/" />;
	};

	const onFinishFailed = (errorInfo) => {
		console.log("Failed:", errorInfo);
	};

	return (
		<div>
			<br />
			<Link to="/login">Already registerd? Login here</Link>
			<Form
				{...layout}
				name="basic"
				initialValues={{ remember: true }}
				onFinish={onFinish}
				onFinishFailed={onFinishFailed}
			>
				<Form.Item
					label="Username"
					name="username"
					rules={[{ required: true, message: "Please input your username!" }]}
				>
					<Input />
				</Form.Item>

				<Form.Item
					label="Email"
					name="email"
					rules={[{ required: true, message: "Please input your email!" }]}
				>
					<Input.Password />
				</Form.Item>

				<Form.Item
					label="Password"
					name="password"
					rules={[{ required: true, message: "Please input your password!" }]}
				>
					<Input.Password />
				</Form.Item>

				<Form.Item {...tailLayout} name="remember" valuePropName="checked">
					<Checkbox>Remember me</Checkbox>
				</Form.Item>

				<Form.Item {...tailLayout}>
					<Button type="primary" htmlType="submit">
						Submit
					</Button>
				</Form.Item>
			</Form>

			<Button
				onClick={Home}
				// onClick={redirect}
				type="primary"
				htmlType="submit"
				style={{ height: "30px", marginTop: "10px" }}
			>
				redirect
			</Button>
		</div>
	);
};
export default Register;
